CREATE TABLE `news` (
  `id` int(3) NOT NULL,
  `title` varchar(50) NOT NULL,
  `date` varchar(20) NOT NULL,
  `text` varchar(1000) NOT NULL,
  `url_img` mediumtext,
  `import_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

--
-- Contenu de la table `news`
--

INSERT INTO `news` (`id`, `title`, `date`, `text`, `url_img`, `import_time`) VALUES
(1, 'Announcing the first shows of the European Tour 20', 'May 13, 2016', 'People of Europe, Nightwish is coming back to play some music for you! Here are the first confirmed shows: 8. Sep. 2016, PT-Lisbon, Coliseu Lisboa Tickets: http://www.bol.pt 10. Sep. 2016,...', 'NULL', '2016-06-11 09:18:05'),
(2, '&ldquo;Fellow Imagineers&rdquo; Trailer', 'April 12, 2016', 'The Nightwish fan-documentary Fellow Imagineers has entered the editing phase and everybody is very excited to see the pieces finally coming together! Big thanks to all fans who participated! Here...', 'NULL', '2016-06-11 09:18:05'),
(3, 'Nightwish wins two Emma awards', 'March 11, 2016', 'Nightwish won two Emmas at the Emma Gaala in Finland!!! Thank you! Or better said: Kiitos paljon!!! We won the Emmas for Metal Album Of The Year and for the Export Of The Year! We unfortunately...', 'NULL', '2016-06-11 09:18:05'),
(4, 'Memories from the Nightwish Cruise', 'January 25, 2016', 'Happy memories from the wonderfully special Nightwish cruise last summer!', 'NULL', '2016-06-11 09:18:05'),
(5, 'Russia &amp; Belarus Tour 2016', 'January 22, 2016', 'Fans in Russia and Belarus, mark your calendars! Nightwish is coming to play some music for you in May 2016! For details and tickets, check our Live page!', 'http://nightwish.com/en/news/2016/01/russia-belarus-tour-2016/nw_russia_2016.jpg,thumbnail', '2016-06-11 09:18:05'),
(6, 'East Asia Tour 2016', 'January 22, 2016', 'People of China, Taiwan and Hong Kong, mark your calendars! Nightwish is heading your way in April for a short East Asian tour. Check the Live page for details!', 'http://nightwish.com/en/news/2016/01/east-asia-tour-2016/nw_eastasia_2016.jpg,thumbnail', '2016-06-11 09:18:05'),
(7, 'USA &amp; Canada Tour 2016', 'January 22, 2016', 'Nightwish will embark on an extensive tour all across the U.S. and Canada starting on February 19th! Supported by the amazing Delain and Sonata Arctica, this is an amazing package that you should...', 'http://nightwish.com/en/news/2016/01/usa-canada-tour-2016/nw_usa_2016-2.jpg,thumbnail', '2016-06-11 09:18:05'),
(8, 'We&rsquo;re coming to CZAD Festival in Poland!', 'January 21, 2016', 'Nightwish will play at CZAD Festival in Poland. The festival will take place from August 25th to 28th. Check the Live page for all upcoming tours and festivals!', 'http://nightwish.com/en/news/2016/01/we-re-coming-to-czad-festival-in-poland/artwork_Czad_Festiwal.jpg,thumbnail', '2016-06-11 09:18:05'),
(9, 'The concerts in France will take place as planned', 'November 15, 2015', 'The first show of our European Tour was a magical evening filled with the universal language of music, goodwill and love. Those things will never be silenced - despite the deeply shocking events in...', 'NULL', '2016-06-11 09:18:05'),
(10, 'Marco on the new Avantasia album', 'November 04, 2015', '“What do Geoff Tate, Dee Snider, Michael Kiske, Robert Mason, Jorn Lande, Bob Catley, Ronnie Atkins, Olli Hartmann and me have in common with Marco Hietala of NIGHTWISH? Yes, we’re all good...', 'http://nightwish.com/en/news/2015/11/marco-on-the-new-avantasia-album/unnamed.jpg,thumbnail', '2016-06-11 09:18:05'),
(11, 'Tuomas comments on Evolution Series by Nightwish', 'October 15, 2015', 'Tuomas was recently interviewed about the Evolution Series by Nightwish jewelry line, check out the video below. You can order your favorites here: The Official Nightwish Shop: http://goo.gl/CjP9s5...', 'NULL', '2016-06-11 09:18:05'),
(12, 'Pre-order Endless Forms Most Beautiful Tour Editio', 'September 18, 2015', 'You can now pre-order Endless Forms Most Beautiful Tour Edition (CD+DVD, release date: 30th of October), re-issued double vinyls and new merchandise from the official Nightwish Shop! Visit the...', 'http://nightwish.com/en/news/2015/09/pre-order-endless-forms-most-beautiful-tour-edition-now/ms-nightwish-tour-edition-lp-banner.jpg,thumbnail', '2016-06-11 09:18:05'),
(13, 'Limited edition Nightwish Golden Charm now availab', 'September 01, 2015', 'KALEVALA JEWELRY will be manufacturing a 14 carat Golden charm, a special addition to the EVOLUTION SERIES BY NIGHTWISH jewelry collection. This unique numbered item is available only from 1st...', 'http://nightwish.com/en/news/2015/09/limited-edition-nightwish-golden-charm-now-available-for-pre-order/ms_nightwish_evolution_series_facebook2.jpg,thumbnail', '2016-06-11 09:18:05'),
(14, 'NIGHTWISH to release »Endless Forms Most Beautiful', 'August 21, 2015', 'Right before the European leg of the »Endless Forms Most Beautiful« tour kicks off, NIGHTWISH will release a special tour edition of the current album. Released on October 30th the tour edition...', 'http://nightwish.com/en/news/2015/08/nightwish-to-release-endless-forms-most-beautiful-tour-edition/tour-edition.png,thumbnail', '2016-06-11 09:18:05'),
(15, 'Evolution Series by Nightwish', 'August 18, 2015', 'Kalevala Koru and Nightwish will launch EVOLUTION SERIES BY NIGHTWISH jewellery collection. You can now pre-order jewellery at the official Nightwish Shop, so go to http://shop.nightwish.com and...', 'http://nightwish.com/en/news/2015/08/evolution-series-by-nightwish/nightwish_evolution_series-2.jpg,thumbnail', '2016-06-11 09:18:05');

